            state.actions.getData2("https://gateway.marvel.com:443GET/v1/public/comics?ts=1&apikey=2c7646b1592cf7f1709584014a70e2b5&hash=e76786c27c33203502f260f6be27384b")
        }, [])

        return (
            <Context.Provider value={state}>
                    <PassedComponen {...props}/>
            </Context.Provider>

        )

    }
    return StoreWrapper;


}

export default InjectContext;
